package com.diploma.black_fox_ex.dto;import lombok.Data;import org.hibernate.validator.constraints.Range;import org.springframework.web.multipart.MultipartFile;import javax.validation.constraints.*;@Datapublic class UserDto {    @NotBlank(message = "{registration.user.username.empty}")    @Size(min = 3, max = 25, message = "{registration.user.username.wrong}")    private String username;    @NotBlank(message = "{registration.user.email.empty}")    @Email(message = "{registration.user.email.wrong}")    private String email;    @NotBlank(message = "{registration.user.password.empty}")    @Pattern(regexp = "((?=.*\\d)(?=.*[a-z])(?=.*[A-Z]).{6,15})", message = "{registration.user.password.wrong}")    private String password;    @NotNull(message = "{registration.user.age.empty}")    @Range(min = 3, max = 110, message = "{registration.user.age.wrong}")    private Byte age;    @NotBlank(message = "{registration.user.sex.empty}")    private String sex;    @NotBlank(message = "{registration.user.info.empty}")    @Size(max = 1000, message = "{registration.user.info.wrong}")    private String info;    @Pattern(regexp = ".*\\B@(?=\\w{5,32}\\b)[a-zA-Z0-9]+(?:_[a-zA-Z0-9]+)*.*", message = "{registration.user.telegram.wrong}")    private String telegram;    private MultipartFile img;    public UserDto(String username, String email, String password, Byte age,                   String sex, String info, String telegram,                   MultipartFile img) {        this.username = username;        this.email = email;        this.password = password;        this.age = age;        this.sex = sex;        this.info = info;        this.telegram = telegram;        this.img = img;    }}