package com.diploma.black_fox_ex.dto;import lombok.Data;import org.hibernate.validator.constraints.Range;import org.springframework.web.multipart.MultipartFile;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;import javax.validation.constraints.Pattern;import javax.validation.constraints.Size;@Datapublic class UserDto {    @Size(min = 3, max = 25, message = "{registration.user.wrong.username}")    private String username;    @Email(message = "{registration.user.wrong.email}")    private String email;    @Pattern(regexp = "((?=.*\\d)(?=.*[a-z])(?=.*[A-Z]).{6,15})", message = "{registration.user.wrong.password}")    private String password;    @Range(min = 3, max = 110, message = "{registration.user.wrong.age}")    private Byte age;    @NotBlank(message = "{registration.user.wrong.sex}")    private String sex;    @NotBlank(message = "{registration.user.wrong.info}")    private String info;    @Pattern(regexp = ".*\\B@(?=\\w{5,32}\\b)[a-zA-Z0-9]+(?:_[a-zA-Z0-9]+)*.*")    private String telegram;    private MultipartFile img;    public UserDto(String username, String email, String password, Byte age,                   String sex, String info, String telegram,                   MultipartFile img) {        this.username = username;        this.email = email;        this.password = password;        this.age = age;        this.sex = sex;        this.info = info;        this.telegram = telegram;        this.img = img;    }}