package com.diploma.black_fox_ex.dto.user;import lombok.Data;import org.hibernate.validator.constraints.Range;import org.springframework.web.multipart.MultipartFile;import javax.validation.constraints.*;@Datapublic class UserDTO {    @NotBlank(message = "registration.user.username.empty")    @Size(min = 3, max = 25, message = "registration.user.username.wrong")    private final String username;    @NotBlank(message = "registration.user.email.empty")    @Email(message = "registration.user.email.wrong")    private final String email;    @NotNull(message = "registration.user.age.empty")    @Range(min = 3, max = 110, message = "registration.user.age.wrong")    private final Number age;    @NotBlank(message = "registration.user.sex.empty")    private final String sex;    @NotBlank(message = "registration.user.info.empty")    @Size(max = 1000, message = "registration.user.info.wrong")    private final String info;    @Pattern(regexp = ".*\\B@(?=\\w{5,32}\\b)[a-zA-Z0-9]+(?:_[a-zA-Z0-9]+)*.*", message = "registration.user.telegram.wrong")    private final String telegram;    @NotBlank(message = "registration.user.password.empty")    @Pattern(regexp = "((?=.*\\d)(?=.*[a-z])(?=.*[A-Z]).{6,35})", message = "registration.user.password.wrong")    private String password;    private MultipartFile img;    private String filename;    public UserDTO(String username, String email, String password, Number age,                   String sex, String info, String telegram,                   MultipartFile img) {        this.username = username;        this.email = email;        this.password = password;        this.age = age;        this.sex = sex;        this.info = info;        this.telegram = telegram;        this.img = img;    }}