package com.diploma.black_fox_ex.controllers.user;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.http.MediaType;import org.springframework.test.web.servlet.MockMvc;import org.springframework.test.web.servlet.request.MockMvcRequestBuilders;import static org.springframework.test.web.servlet.result.MockMvcResultHandlers.print;import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;@SpringBootTest@AutoConfigureMockMvcclass RegistrationControllerTest {    @Autowired    MockMvc mockMvc;    @Test    void addNewUser() throws Exception {        this.mockMvc.perform(MockMvcRequestBuilders.post("/registration/add")                        .accept(MediaType.TEXT_HTML)                        .param("username", "111")                        .param("email", "test123")                        .param("password", "test123")                        .param("age", "test123")                        .param("sex", "test123")                        .param("info", "test123")                        .param("telegram", "test123")                        .param("img", "test123"))                .andExpect(status().isOk())                .andDo(print());    }}